import {useDispatch, useSelector} from "react-redux";
import React, {useEffect, useState} from 'react';
import {MDBContainer, MDBRow, MDBCol} from 'mdb-react-ui-kit';
import {
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardTitle,
    MDBCardText,
    MDBRipple,
    MDBBtn
} from 'mdb-react-ui-kit';
import {getVilla} from "../redux/action/villa";
import {
    MDBModal,
    MDBModalDialog,
    MDBModalContent,
    MDBModalHeader,
    MDBModalTitle,
    MDBModalBody,
    MDBModalFooter,
} from 'mdb-react-ui-kit';

export default function Villa() {
    const villa = useSelector(state => state.villa);
    console.log(villa.content)
    console.log(villa)
    const dispatch = useDispatch();
    const [topRightModal, setTopRightModal] = useState(false);
    const [villaModal, setVillaModal] = useState({});

    const renderStar = (star) => {
        switch (star) {
            case "Ba sao":
                return <span><i className="fa-solid fa-star"></i><i className="fa-solid fa-star"></i><i
                    className="fa-solid fa-star"></i><i className="fa-regular fa-star"></i><i
                    className="fa-regular fa-star"></i></span>
            case "Bốn sao":
                return <span><i className="fa-solid fa-star"></i><i className="fa-solid fa-star"></i><i
                    className="fa-solid fa-star"></i><i className="fa-solid fa-star"></i><i
                    className="fa-regular fa-star"></i></span>
            case "Năm sao":
                return <span><i className="fa-solid fa-star"></i><i className="fa-solid fa-star"></i><i
                    className="fa-solid fa-star"></i><i className="fa-solid fa-star"></i>
                <i className="fa-solid fa-star"></i></span>
            default:
                return null;
        }
    }
    const toggleOpen = (id) => {
        setTopRightModal(!topRightModal);
        const villaModal1 = villa.find(v => v.id === id);
        setVillaModal(villaModal1);
    };

    const toggleClose = () => {
        setTopRightModal(!topRightModal);
    }
    useEffect(() => {
        dispatch(getVilla());
    }, []);
    return <MDBContainer>
        <MDBRow>
            {villa.map(v => (
                <MDBCol className="col-md-4 mt-5" size='md'>
                    <MDBCard>
                        <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
                            <MDBCardImage src={v.photo} fluid alt='...'/>
                            <a>
                                <div className='mask' style={{backgroundColor: 'rgba(251, 251, 251, 0.15)'}}></div>
                            </a>
                        </MDBRipple>
                        <MDBCardBody>
                            <MDBCardTitle>{v.serviceName}</MDBCardTitle>
                            <MDBCardText>
                                {v.description}
                            </MDBCardText>
                            <MDBCardTitle style={{color: "red"}}>Chí phí thuê: {v.rentalCost.toLocaleString('vi-VN', {
                                style: 'currency',
                                currency: 'VND'
                            })}/{v.rentalTypeName}</MDBCardTitle><br/>
                            <MDBBtn onClick={() => toggleOpen(v.id)}>Chi tiết</MDBBtn>
                        </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            ))}
        </MDBRow>
        <MDBModal
            animationDirection='right'
            open={topRightModal}
            tabIndex='-1'
            setOpen={setTopRightModal}
        >
            <MDBModalDialog position='top-right' side>
                <MDBModalContent>
                    <MDBModalHeader className='bg-info text-white'>
                        <MDBModalTitle>{villaModal.serviceName}</MDBModalTitle>
                        <MDBBtn
                            color='none'
                            className='btn-close btn-close-white'
                            onClick={toggleClose}
                        ></MDBBtn>
                    </MDBModalHeader>
                    <MDBModalBody>
                        <div className='row'>
                            <div className='col-5 text-center'>
                                <MDBCardImage src={villaModal.photo} fluid alt='...'/>
                                <MDBCardImage style={{width: "75%", marginTop: "20px"}}
                                              src="https://d3nax9sawz4kbl.cloudfront.net/company_logos/e8519e0026f93e93a626c8744cdf0977.png"
                                              fluid alt='...'/>
                            </div>

                            <div className='col-7'>
                                <p>Loại dịch vụ: {villaModal.serviceTypeName}</p>
                                <p>Số người tối đa: {villaModal.maxPeople}</p>
                                <p>Số tầng: {villaModal.floor}</p>
                                <p>Diện tích: {villaModal.area}</p>
                                <p>Hồ bơi: {villaModal.poolArea}</p>
                                <p>Tiêu chuẩn: {}</p>
                                <p>Chi phí thuê: {villaModal.rentalCost.toLocaleString('vi-VN', {
                                    style: 'currency',
                                    currency: 'VND'
                                })}/{villaModal.rentalTypeName}</p>
                            </div>
                        </div>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color='info'>Go to the cart</MDBBtn>
                        <MDBBtn outline color='info' onClick={toggleClose}>
                            Close
                        </MDBBtn>
                    </MDBModalFooter>
                </MDBModalContent>
            </MDBModalDialog>
        </MDBModal>
    </MDBContainer>;
}
