import {useDispatch, useSelector} from "react-redux";
import React, {useEffect, useState} from 'react';
import {MDBContainer, MDBRow, MDBCol} from 'mdb-react-ui-kit';
import {
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardTitle,
    MDBCardText,
    MDBRipple,
    MDBBtn
} from 'mdb-react-ui-kit';
import {getVilla} from "../redux/action/villa";
import {
    MDBModal,
    MDBModalDialog,
    MDBModalContent,
    MDBModalHeader,
    MDBModalTitle,
    MDBModalBody,
    MDBModalFooter,
} from 'mdb-react-ui-kit';

export default function Villa() {
    const villa = useSelector(state => state.villa);
    console.log(villa.content)
    console.log(villa)
    const dispatch = useDispatch();
    const [topRightModal, setTopRightModal] = useState(false);
    const [villaModal, setVillaModal] = useState({});
    const toggleOpen = (id) => {setTopRightModal(!topRightModal);
    const villaModal1 = villa.reduce(v => v.id = id);
    setVillaModal(villaModal1);
    };

    useEffect(() => {
        dispatch(getVilla());
    }, []);
    return <MDBContainer>
        <MDBRow>
            {villa.map(v => (
                <MDBCol className="col-md-4 mt-5" size='md'>
                    <MDBCard>
                        <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
                            <MDBCardImage src={v.photo} fluid alt='...'/>
                            <a>
                                <div className='mask' style={{backgroundColor: 'rgba(251, 251, 251, 0.15)'}}></div>
                            </a>
                        </MDBRipple>
                        <MDBCardBody>
                            <MDBCardTitle>{v.serviceName}</MDBCardTitle>
                            <MDBCardText>
                                {v.description}
                            </MDBCardText>
                            <MDBCardTitle style={{color: "red"}}>Chí phí thuê: {v.rentalCost.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}/{v.rentalTypeName}</MDBCardTitle><br/>
                            <MDBBtn onClick={() => toggleOpen(v.id)}>Top right</MDBBtn>
                        </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            ))}
        </MDBRow>
        <MDBModal
            animationDirection='right'
            open={topRightModal}
            tabIndex='-1'
            setOpen={setTopRightModal}
        >
            <MDBModalDialog position='top-right' side>
                <MDBModalContent>
                    <MDBModalHeader className='bg-info text-white'>
                        <MDBModalTitle>{villaModal.serviceName}</MDBModalTitle>
                        <MDBBtn
                            color='none'
                            className='btn-close btn-close-white'
                            onClick={toggleOpen}
                        ></MDBBtn>
                    </MDBModalHeader>
                    <MDBModalBody>
                        <div className='row'>
                            <div className='col-3 text-center'>
                                <i className='fas fa-shopping-cart fa-4x text-info'></i>
                            </div>

                            <div className='col-9'>
                                <p>Do you need more time to make a purchase decision?</p>
                                <p>No pressure, your product will be waiting for you in the cart.</p>
                            </div>
                        </div>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color='info'>Go to the cart</MDBBtn>
                        <MDBBtn outline color='info' onClick={toggleOpen}>
                            Close
                        </MDBBtn>
                    </MDBModalFooter>
                </MDBModalContent>
            </MDBModalDialog>
        </MDBModal>
    </MDBContainer>;
}
